    // Allocate memory for n integers
    ptr = (int*) malloc(n * sizeof(int));

    if (ptr == NULL) {
        printf("Memory allocation failed!\n");
        return -1;
    }

    // Use the allocated memory
    for (int i = 0; i < n; i++) {
        ptr[i] = (i + 1) * 10;
