#include <stdio.h>
#include <stdlib.h>

int main() {
    int *ptr;
    int n = 5;

    // Allocate and initialize memory using calloc
    ptr = (int*) calloc(n, sizeof(int));

    if (ptr == NULL) {
        printf("Memory allocation failed!\n");
        return -1;
    }

    printf("Memory allocated by calloc (initialized to 0):\n");
    for (int i = 0; i < n; i++) {
        printf("%d ", ptr[i]);  // All values are 0
    }
    printf("\n");

    // Resize the memory block using realloc
    n = 10;
    ptr = (int*) realloc(ptr, n * sizeof(int));

    if (ptr == NULL) {
        printf("Memory reallocation failed!\n");
        return -1;
    }

    // Initialize new elements
    for (int i = 5; i < n; i++) {
        ptr[i] = (i + 1) * 10;
    }

    printf("After reallocation:\n");
    for (int i = 0; i < n; i++) {
        printf("%d ", ptr[i]);
    }
    printf("\n");

    // Free the memory
    free(ptr);

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main() {
    int *ptr;

    // Memory leak example (BAD - memory not freed)
    ptr = (int*) malloc(5 * sizeof(int));
    ptr = (int*) malloc(10 * sizeof(int));  // Previous memory leaked!
    free(ptr);

    // Dangling pointer example (BAD)
    ptr = (int*) malloc(5 * sizeof(int));
    free(ptr);
    // *ptr = 10;  // Dangerous! ptr is now a dangling pointer

    // Buffer overflow example (BAD)
    int arr[5] = {1, 2, 3, 4, 5};
    // arr[10] = 100;  // Buffer overflow! Writing beyond array bounds

    // Correct approach
    ptr = (int*) malloc(5 * sizeof(int));
    if (ptr != NULL) {
        for (int i = 0; i < 5; i++) {
            ptr[i] = i + 1;
        }
        free(ptr);
        ptr = NULL;  // Prevent dangling pointer
    }

    return 0;
}

