#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int *data;
    int size;
    int capacity;
} DynamicArray;

void initArray(DynamicArray *arr, int initialCapacity) {
    arr->data = (int*) malloc(initialCapacity * sizeof(int));
    arr->size = 0;
    arr->capacity = initialCapacity;
}

void resizeArray(DynamicArray *arr) {
    arr->capacity *= 2;
    arr->data = (int*) realloc(arr->data, arr->capacity * sizeof(int));
}

void addElement(DynamicArray *arr, int element) {
    if (arr->size >= arr->capacity) {
        resizeArray(arr);
    }
    arr->data[arr->size] = element;
    arr->size++;
}

void removeElement(DynamicArray *arr, int index) {
    if (index < 0 || index >= arr->size) {
        printf("Invalid index!\n");
        return;
    }

    for (int i = index; i < arr->size - 1; i++) {
        arr->data[i] = arr->data[i + 1];
    }
    arr->size--;
}

void printArray(DynamicArray *arr) {
    printf("Array (size: %d, capacity: %d): ", arr->size, arr->capacity);
    for (int i = 0; i < arr->size; i++) {
        printf("%d ", arr->data[i]);
    }
    printf("\n");
}

void freeArray(DynamicArray *arr) {
    free(arr->data);
    arr->data = NULL;
    arr->size = 0;
    arr->capacity = 0;
}

int main() {
    DynamicArray arr;
    initArray(&arr, 2);

    int choice, element, index;

    while (1) {
        printf("\nDynamic Array Manager\n");
        printf("1. Add element\n");
        printf("2. Remove element\n");
        printf("3. Print array\n");
        printf("4. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);

        switch (choice) {
            case 1:
                printf("Enter element to add: ");
                scanf("%d", &element);
                addElement(&arr, element);
                break;
            case 2:
                printf("Enter index to remove: ");
                scanf("%d", &index);
                removeElement(&arr, index);
                break;
            case 3:
                printArray(&arr);
                break;
            case 4:
                freeArray(&arr);
                exit(0);
            default:
                printf("Invalid choice!\n");
        }
    }

    return 0;
}
