#include <stdio.h>

int main() {
    // Integer types
    int age = 25;
    short smallNum = 100;
    long bigNum = 123456789L;

    // Floating-point types
    float price = 19.99f;
    double precisePrice = 19.99;

    // Character type
    char grade = 'A';

    // Printing values
    printf("Age: %d\n", age);
    printf("Price: %.2f\n", price);
    printf("Grade: %c\n", grade);

    return 0;
}

#include <stdio.h>

int main() {
    int a = 10, b = 5;

    // Arithmetic operators
    printf("a + b = %d\n", a + b);
    printf("a - b = %d\n", a - b);
    printf("a * b = %d\n", a * b);
    printf("a / b = %d\n", a / b);
    printf("a %% b = %d\n", a % b);

    // Comparison operators
    printf("a > b: %d\n", a > b);
    printf("a == b: %d\n", a == b);

    // Logical operators
    printf("(a > 0) && (b > 0): %d\n", (a > 0) && (b > 0));

    return 0;
}

#include <stdio.h>

int main() {
    int num = 15;

    // If-else statement
    if (num > 0) {
        printf("Positive number\n");
    } else if (num < 0) {
        printf("Negative number\n");
    } else {
        printf("Zero\n");
    }

    // For loop
    for (int i = 1; i <= 5; i++) {
        printf("%d ", i);
    }
    printf("\n");

    // While loop
    int i = 1;
    while (i <= 5) {
        printf("%d ", i);
        i++;
    }
    printf("\n");

    // Switch statement
    int day = 3;
    switch (day) {
        case 1:
            printf("Monday\n");
            break;
        case 2:
            printf("Tuesday\n");
            break;
        case 3:
            printf("Wednesday\n");
            break;
        default:
            printf("Other day\n");
    }

    return 0;
}

