#include <stdio.h>

// Pass by value
void increment(int x) {
    x = x + 1;
    printf("Inside function: %d\n", x);
}

// Pass by reference using pointers
void incrementPtr(int *x) {
    *x = *x + 1;
    printf("Inside function: %d\n", *x);
}

int main() {
    int num = 5;

    printf("Before: %d\n", num);
    increment(num);
    printf("After pass by value: %d\n", num);

    incrementPtr(&num);
    printf("After pass by reference: %d\n", num);

    return 0;
}

#include <stdio.h>

int main() {
    // Declaring and initializing an array
    int numbers[5] = {10, 20, 30, 40, 50};

    // Accessing array elements
    for (int i = 0; i < 5; i++) {
        printf("Element [%d]: %d\n", i, numbers[i]);
    }

    // 2D Array
    int matrix[3][3] = {
        {1, 2, 3},
        {4, 5, 6},
        {7, 8, 9}
    };

    printf("\nMatrix:\n");
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            printf("%d ", matrix[i][j]);
        }
        printf("\n");
    }

    return 0;
}

#include <stdio.h>
#include <string.h>

int main() {
    // Declaring strings
    char str1[] = "Hello";
    char str2[20] = "World";
    char str3[20];

    // String functions
    strcpy(str3, str1);  // Copy str1 to str3
    strcat(str3, " ");   // Concatenate space
    strcat(str3, str2);  // Concatenate str2

    printf("str1: %s\n", str1);
    printf("str2: %s\n", str2);
    printf("str3: %s\n", str3);
    printf("Length of str3: %lu\n", strlen(str3));

    // Comparing strings
    if (strcmp(str1, str2) == 0) {
        printf("Strings are equal\n");
    } else {
        printf("Strings are different\n");
    }

    return 0;
}

#include <stdio.h>

int main() {
    int num = 42;
    int *ptr;  // Pointer to integer

    ptr = &num;  // Store address of num in ptr

    printf("Value of num: %d\n", num);
    printf("Address of num: %p\n", &num);
    printf("Value of ptr: %p\n", ptr);
    printf("Value pointed by ptr: %d\n", *ptr);

    // Pointer arithmetic
    int arr[] = {10, 20, 30, 40, 50};
    int *arrPtr = arr;

    printf("\nArray elements using pointer:\n");
    for (int i = 0; i < 5; i++) {
        printf("Element %d: %d\n", i, *(arrPtr + i));
    }

    return 0;
}

#include <stdio.h>
#include <string.h>

// Define a structure
struct Student {
    int id;
    char name[50];
    float marks;
};

int main() {
    // Declare and initialize structure variables
    struct Student s1 = {1, "John Doe", 85.5};

    // Accessing structure members
    printf("ID: %d\n", s1.id);
    printf("Name: %s\n", s1.name);
    printf("Marks: %.2f\n", s1.marks);

    // Modifying structure members
    s1.marks = 90.0;
    printf("Updated Marks: %.2f\n", s1.marks);

    return 0;
}

#include <stdio.h>

union Data {
    int i;
    float f;
    char str[20];
};

int main() {
    union Data data;

    data.i = 10;
    printf("data.i: %d\n", data.i);

    data.f = 220.5;
    printf("data.f: %f\n", data.f);

    strcpy(data.str, "C Programming");
    printf("data.str: %s\n", data.str);

    // The value of i and f is lost after assigning str
    printf("data.i: %d\n", data.i);
    printf("data.f: %f\n", data.f);

    return 0;
}

#include <stdio.h>

enum Weekday {
    MONDAY,
    TUESDAY,
    WEDNESDAY,
    THURSDAY,
    FRIDAY,
    SATURDAY,
    SUNDAY
};

int main() {
    enum Weekday today = WEDNESDAY;

    printf("Today is day number: %d\n", today);

    switch(today) {
        case MONDAY:
            printf("It's Monday!\n");
            break;
        case WEDNESDAY:
            printf("It's Wednesday!\n");
            break;
        default:
            printf("It's another day.\n");
    }

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main() {
    int n = 5;

    // Using malloc
    int *ptr1 = (int*) malloc(n * sizeof(int));

    if (ptr1 == NULL) {
        printf("Memory allocation failed!\n");
        return -1;
    }

    printf("Memory allocated using malloc.\n");

    // Initialize values
    for (int i = 0; i < n; i++) {
        ptr1[i] = i + 1;
    }

    // Print values
    for (int i = 0; i < n; i++) {
        printf("%d ", ptr1[i]);
    }
    printf("\n");

    // Using calloc
    int *ptr2 = (int*) calloc(n, sizeof(int));

    if (ptr2 == NULL) {
        printf("Memory allocation failed!\n");
        free(ptr1);
        return -1;
    }

    printf("Memory allocated using calloc.\n");

    // Print values (initialized to 0)
    for (int i = 0; i < n; i++) {
        printf("%d ", ptr2[i]);
    }
    printf("\n");

    // Using realloc
    ptr1 = (int*) realloc(ptr1, (n + 5) * sizeof(int));

    if (ptr1 == NULL) {
        printf("Memory reallocation failed!\n");
        free(ptr2);
        return -1;
    }

    printf("Memory reallocated.\n");

    // Free allocated memory
    free(ptr1);
    free(ptr2);

    printf("Memory freed.\n");

    return 0;
}

#include <stdio.h>
#include <stdlib.h>

int main() {
    FILE *fp;
    char data[100];

    // Writing to a file
    fp = fopen("example.txt", "w");

    if (fp == NULL) {
        printf("Error opening file for writing!\n");
        return -1;
    }

    fprintf(fp, "This is a sample file.\n");
    fprintf(fp, "It contains multiple lines.\n");

    fclose(fp);

    // Reading from a file
    fp = fopen("example.txt", "r");

    if (fp == NULL) {
        printf("Error opening file for reading!\n");
        return -1;
    }

    while (fgets(data, 100, fp) != NULL) {
        printf("%s", data);
    }

    fclose(fp);

    return 0;
}
