#include <stdio.h>
#include <string.h>

#define MAX_ACCOUNTS 100
#define NAME_LENGTH 50

struct Account {
    int accountNumber;
    char name[NAME_LENGTH];
    double balance;
};

void createAccount(struct Account accounts[], int *count);
void deposit(struct Account accounts[], int count);
void withdraw(struct Account accounts[], int count);
void checkBalance(struct Account accounts[], int count);

int main() {
    struct Account accounts[MAX_ACCOUNTS];
    int count = 0;
    int choice;

    while (1) {
        printf("\n--- Bank Management System ---\n");
        printf("1. Create Account\n");
        printf("2. Deposit\n");
        printf("3. Withdraw\n");
        printf("4. Check Balance\n");
        printf("5. Exit\n");
        printf("Enter your choice: ");
        scanf("%d", &choice);

        switch (choice) {
            case 1:
                createAccount(accounts, &count);
                break;
            case 2:
                deposit(accounts, count);
                break;
            case 3:
                withdraw(accounts, count);
                break;
            case 4:
                checkBalance(accounts, count);
                break;
            case 5:
                printf("Thank you for using our bank!\n");
                return 0;
            default:
                printf("Invalid choice!\n");
        }
    }

    return 0;
}

void createAccount(struct Account accounts[], int *count) {
    if (*count >= MAX_ACCOUNTS) {
        printf("Cannot create more accounts. Limit reached.\n");
        return;
    }

    printf("Enter account number: ");
    scanf("%d", &accounts[*count].accountNumber);
    printf("Enter account holder name: ");
    scanf("%s", accounts[*count].name);
    accounts[*count].balance = 0.0;

    printf("Account created successfully!\n");
    printf("Account Number: %d\n", accounts[*count].accountNumber);
    printf("Account Holder: %s\n", accounts[*count].name);
    printf("Balance: $%.2f\n", accounts[*count].balance);

    (*count)++;
}

void deposit(struct Account accounts[], int count) {
    int accountNumber;
    double amount;

    printf("Enter account number: ");
    scanf("%d", &accountNumber);

    for (int i = 0; i < count; i++) {
        if (accounts[i].accountNumber == accountNumber) {
            printf("Enter amount to deposit: $");
            scanf("%lf", &amount);

            if (amount > 0) {
                accounts[i].balance += amount;
                printf("Deposit successful!\n");
                printf("New Balance: $%.2f\n", accounts[i].balance);
            } else {
                printf("Invalid amount!\n");
            }
            return;
        }
    }

    printf("Account not found!\n");
}

void withdraw(struct Account accounts[], int count) {
    int accountNumber;
    double amount;

    printf("Enter account number: ");
    scanf("%d", &accountNumber);

    for (int i = 0; i < count; i++) {
        if (accounts[i].accountNumber == accountNumber) {
            printf("Enter amount to withdraw: $");
            scanf("%lf", &amount);

            if (amount > 0 && amount <= accounts[i].balance) {
                accounts[i].balance -= amount;
                printf("Withdrawal successful!\n");
                printf("New Balance: $%.2f\n", accounts[i].balance);
            } else if (amount > accounts[i].balance) {
                printf("Insufficient balance!\n");
            } else {
                printf("Invalid amount!\n");
            }
            return;
        }
    }

    printf("Account not found!\n");
}

void checkBalance(struct Account accounts[], int count) {
    int accountNumber;

    printf("Enter account number: ");
    scanf("%d", &accountNumber);

    for (int i = 0; i < count; i++) {
        if (accounts[i].accountNumber == accountNumber) {
            printf("\n--- Account Details ---\n");
            printf("Account Number: %d\n", accounts[i].accountNumber);
            printf("Account Holder: %s\n", accounts[i].name);
            printf("Balance: $%.2f\n", accounts[i].balance);
            return;
        }
    }

    printf("Account not found!\n");
}
